dt-option.example:
$('#example').DataTable({
    searchPanes: true,
    dom: 'Plfrtip',
    columnDefs: [
        {
            searchPanes: {
                className: 'myCustomClass'
            },
            targets: [3]
        }
    ]
});

table = $('#example').DataTable({
    dom: 'Plfrtip',
    searchPanes: {
        panes: [
            {
                header: 'Custom',
                options: [
                    {
                        label: 'Accountants from Tokyo',
                        value: function(rowData, rowIdx) {
                            return rowData[1] === 'Accountant' && rowData[2] === 'Tokyo';
                        }
                    }
                ],
                className: "PaneSpecific"
            }
        ]
    }
});
