dt-option.example:
$(document).ready(function() {
    $('#example').DataTable( {
        language: {
            searchPanes: {
                collapse: {0: 'Search Options', _: 'Search Options (%d)'}
            }
        },
        buttons:[
            'searchPanes'
        ],
        dom: 'Bfrtip',
    });
});

$(document).ready(function() {
    $('#example').DataTable( {
        language: {
            searchPanes: {
                collapse: 'Search Options"
            }
        },
        buttons:[
            'searchPanes'
        ],
        dom: 'Bfrtip',
    });
});
