dt-option.example:
$(document).ready(function() {
    $('#example').DataTable( {
        dom: 'Pfrtip',
        language: {
            searchPanes: {
                title:{
                    _: 'Filters Selected - %d',
                    0: 'No Filters Selected',
                    1: 'One Filter Selected',
                }
            }
        }
    });
});

$(document).ready(function() {
    $('#example').DataTable( {
        dom: 'Pfrtip',
        language: {
            searchPanes: {
                title: 'Select filters below'
            }
        }
    });
});
