dt-option.example:
$(document).ready(function() {
    $('#example').DataTable({
        dom: 'Plfrtip',
        language: {
            searchPanes: {
                title: {
                    _: 'Filters Selected - %d',
                    0: 'No Filters Selected',
                    1: 'One Filter Selected'
                }
            }
        }
    });
});

$(document).ready(function() {
    $('#example').DataTable({
        searchPanes: {
            viewTotal: true
        },
        dom: 'Plfrtip',
        language: {
            searchPanes: {
                count: '{total} found',
                countFiltered: '{shown} ({total})'
            }
        }
    });
});
