dt-option.example:
new DataTable('#myTable', {
	layout: {
		top1: {
			searchPanes: {
				emptyMessage: '</i></b>EMPTY</b></i>'
			}
		}
	}
});

var table = new DataTable('#myTable', {
	layout: {
		top1: 'searchPanes'
	},
	columnDefs: [
		{
			searchPanes: {
				emptyMessage: '</i></b>EMPTY</b></i>'
			},
			targets: [2]
		}
	]
});

table.cell(2, 2).data('');
table.cell(6, 5).data('');
table.cell(2, 3).data('');
table.cell(2, 0).data('');
table.cell(6, 1).data('');
table.cell(2, 4).data('');
table.searchPanes.rebuildPane();
