dt-option.example:
$(document).ready(function() {
    $('#example').DataTable( {
        searchPanes: {
            panes: [
                {
                    options: [
                        {
                            label:'Accountants in Tokyo',
                            value: function(rowData, rowIdx){
                                return rowData[2] === 'Accountant' && rowData[3] === 'Tokyo';
                            },
                            className: 'tokyo
                        }
                    ]
                }
            ]
        },
        dom: 'Pfrtip'
    });
});
