dt-example.js:
$(document).ready(function() {
    let table = $('#example').DataTable( {
        dom: 'Pfrtip',
        data:[]
    });
    let data = table.rows().data().toArray();
    table.clear();
    for(let i = 0; i < 5000; i++){
        table.row.add([i, i, i, i%10, i%100, i%50]);
    }
    table.searchPanes.rebuildPane();
    table.draw();
});
