dt-example.js:
$(document).ready(function() {
    let table = $('#example').DataTable();
    let data = table.rows().data().toArray();
    table.draw();
     $('#rebuild').click(() => {
        table.clear();
        let t0 = performance.now();
        for(let i = 0; i < 100; i++){
            table.rows.add(data);
        }
        let t1 = performance.now();
        table.draw();
        let t2 = performance.now();
        console.table([['add', t1-t0], ['draw', t2-t1]]);
    });
});
