dt-example.js:
$(document).ready(function() {
    let table = $('#example').DataTable( {
        searchPanes:{
            columns:[3,2,1],
			viewTotal: true
        },
        dom: 'Pfrtip'
    });
    let data = table.rows().data().toArray();
    table.clear();
    for(let i = 0; i < 100; i++){
        table.rows.add(data);
    }
    table.searchPanes.rebuildPane();
    table.draw();
});
