dt-example.js:
$(document).ready(function() {
    $('#example').DataTable( {
        searchPane:{
            viewTotal: true,
        },
        columnDefs:[
            {
                searchPane: {
                    options:[
                        {
                            label: 'UK',
                            value:['Edinburgh', 'London'],
                            condition:'||',
                        },
                    ],
                },
                targets: [3]
            }
        ]
    });
});
