dt-example.js:
$(document).ready(function() {
    var dt = $('#example').DataTable( {
        searchPanes:{
            emptyMessage:"<i><b>EMPTY</b></i>",
        },
        dom: 'Pfrtip',
    });

    dt.cell(2,2).data('');
    dt.cell(4,2).data('Edi');
    dt.cell(6,2).data('');
    dt.cell(2,3).data('');
    dt.searchPanes.rebuildPane(3);
    dt.searchPanes.rebuildPane(2);
});
