$(document).ready( function () {
  var table = $('#example').DataTable({
    ajax: '../resources/arrayDataEntities.json',
    dom: 'Plfrtip',
    columns: [
			{ 
				data: "users.first_name"
			},
			{ 
				data: "users.last_name"
			},
			{ 
				data: "sites.name",
        searchPanes: {
          show: true
        }
			},
			{ 
				data: "permission",
				render: {
					_: '[, ].name',
					sp: '[].name'
				},
				searchPanes: {
					orthogonal: 'sp',
          			show: true
				}
			}
		]
  });
} );
