dt-example.js:
$(document).ready( function () {
  var table = $('#example').DataTable({
    ajax: '../resources/arrayDataHTML.json',
    dom: 'Plfrtip',
    columns: [
			{ 
				data: "users.first_name"
			},
			{ 
				data: "users.last_name"
			},
			{ 
				data: "sites.name",
				searchPanes: {
					show: true
				},
				render: $.fn.dataTable.render.text()
			},
			{ 
				data: "permission",
				render: {
					_: '[, ].name',
					sp: '[].name'
				},
				searchPanes: {
					orthogonal: 'sp',
					show: true
				}
			}
		]
  });
} );
