dt-example.js:
$(document).ready(function() {

    $('#example').DataTable( {
        dom: 'PBfrtip',
        ajax: {
            url: "../../../Editor-PHP-Demo/controllers/searchPanes.php",
            type: "POST"
        },    
        columns: [
            { data: "users.first_name" },
            { data: "users.last_name" },
            { data: "users.phone" },
            { data: "sites.name" }
        ],
        columnDefs:[{
            searchPanes:{
                show: true,
            },
            targets: [0, 1, 2, 3],
        }],
        serverSide: true,
        select: true
    } );
} );
