dt-example.js:
$(document).ready(function() {
    var dt = $('#example').DataTable( {
        searchPanes: {
            viewTotal: true,
        },
        columnDefs:[ 
            {
                orderable: false,
                className: 'select-checkbox',
                searchPanes: {
                    show: true,
                    options:[
                        {
                            label: 'Checked',
                            value: function(rowData,rowIdx){
                                return this.row(rowIdx, {selected: true}).any();
                            },
                        },
                        {
                            label: 'Un-Checked',
                            value: function(rowData, rowIdx){
                                return this.row(rowIdx, {selected: true}).any() === false;
                            },
                        },
                    ],
                },
                targets: [0]
            },
            {
                searchPanes: {
                    options:[
                        {
                            label: 'Under 20',
                            value: 20,
                            condition:'<',
                        },
                        {
                            label: '20 to 30',
                            value: function(rowData, rowIdx){
                                return rowData[4] <= 30 && rowData[4] >=20;
                            }
                        },
                         {
                            label: '30 to 40',
                            value: function(rowData, rowIdx){
                                return rowData[4] <= 40 && rowData[4] >=30;
                            }
                        },
                         {
                            label: '40 to 50',
                            value: function(rowData, rowIdx){
                                return rowData[4] <= 50 && rowData[4] >=40;
                            }
                        },
                         {
                            label: '50 to 60',
                            value: function(rowData, rowIdx){
                                return rowData[4] <= 60 && rowData[4] >=50;
                            }
                        },
                         {
                            label: 'Over 60',
                            value: 60,
                            condition:'>',
                        },
                    ]
                },
                targets:[4],
            },
            {
                searchPanes: {
                    options:[
                        {
                            label: 'Not Edinburgh',
                            value: 'Edinburgh',
                            condition: '!=',
                        },
                        {
                            label: 'Not London',
                            value: function(rowData, rowIdx){
                                return rowData[3] !== 'London';
                            }
                        }
                    ],
                },
                targets: [3]
            }
        ],
        select: {
			style:    'os',
			selector: 'td:first-child'
		},
		order: [[ 1, 'asc' ]]
    });

    dt.on('select.dt', () => {			
        dt.searchPanes.rebuildPane(0);
    });

    dt.on('deselect.dt', () => {
        dt.searchPanes.rebuildPane(0);
    });
});
