dt-example.js:
$(document).ready(function() {
    $('#example').DataTable( {
        searchPanes:{
            panes: [
                {
                    header:'Custom',
                    searchPanes:{
                        options:[
                            {
                                label:'Accountants from Tokyo',
                                value: function(rowData, rowIdx){
                                    return rowData[2] === 'Accountant' && rowData[3] === 'Tokyo';
                                }
                            }
                        ]
                    },
                    dtOpts:{
                        searching: false,
                        order: [[1, 'desc']]
                    }
                }
            ],
            displayColumns: 4,
        },
        dom: 'Sfrtip',
    });
});
