dt-example.js:
$(document).ready(function() {
    $('#example').DataTable({
        searchPanes: {
            panes: [
                {
                    header: 'Age Range',
                    options: [
                        {
                            label: 'Over 50',
                            value: function(rowData, rowIdx) {
                                return rowData[3] > 50;
                            }
                        },
                        {
                            label: 'Under 50',
                            value: function(rowData, rowIdx) {
                                return rowData[3] < 50;
                            }
                        }
                    ],
                    preSelect: ['Over 50']
                }
            ]
        },
        dom: 'Plfrtip'
    });
});
