dt-example.js:
$(document).ready(function() {
    table = $('#example').DataTable({
        dom: 'Sfrtip',
        searchPanes: {
            panes: [
                {
                    header: 'unittest header',
                    searchPanes: {
                        options: [
                            {
                                label: 'unittest label',
                                value: function(rowData, rowIdx) {
                                    return rowData[2] === 'San Francisco' && rowData[3] === '66';
                                }
                            }
                        ]
                    }
                }
            ]
        }
    });
});
