dt-example.js:
$(document).ready(function() {
    $('#example').DataTable( {
        dom: 'Sfrtip',
        language: {
            searchPanes: {
                emptyPanes: 'There are no panes to display. :/'
            }
        }
    });
    $('#populate').click(function(){
        for(let i = 0; i < 50; i++){
            $('#example').DataTable().row.add([i%1, i%2, i%3, i%4, i%5, i%6]).draw();   
        }
    });
    $('#rebuildPane').click(function(){
        $('#example').DataTable().searchPanes.rebuildPane();
    })
});
