dt-example.js:
$(document).ready(function() {
    table = $('#example').DataTable({
        dom: 'Pfrtip',
        searchPanes: {
            panes: [
                {
                    header:'Custom',
                    options:[
                        {
                            label:'Accountants from Tokyo',
                            value: function(rowData, rowIdx){
                                return rowData[1] === 'Accountant' && rowData[2] === 'Tokyo';
                            }
                        }
                    ],
                    dtOpts:{
                        paging: true,
                        order: [[1, 'desc']]
                    },
                    className:"AllCustom"
                }
            ],
        }
    });
});
