dt-example.js:
$(document).ready(function() {
    $('#example').DataTable( {
        searchPanes:{
            panes: [
                {
                    header:'Custom',
                    options:[
                        {
                            label:'Accountants from Tokyo',
                            value: function(rowData, rowIdx){
                                return rowData[1] === 'Accountant' && rowData[2] === 'Tokyo';
                            }
                        }
                    ],
                    dtOpts: {
                        dom:"tp",
                        paging: true,
                        order: [[1, 'desc']]
                    },
                    className: 'myCustomClass'
                }
            ],
        },
        dom: 'Plfrtip',
        columnDefs:[
            {
                searchPanes: {
                    className: 'myCustomClass',
                },
                targets: [3]
            }
        ]
    });
});
