dt-example.js:
$(document).ready(function() {
    $('#example').DataTable( {
        dom: 'Pfrtip',
        searchPanes:{
            order: ['ageName', 'Custom Name', 'Position'],
            panes: [
                {
                    name:'Custom Name',
                    header:'Custom',
                    options:[
                        {
                            label:'Accountants from Tokyo',
                            value: function(rowData, rowIdx){
                                return rowData[1] === 'Accountant' && rowData[2] === 'Tokyo';
                            }
                        }
                    ]
                }
            ],
        },
        columnDefs:[
            {
                searchPanes: {
                    name: 'ageName'
                },
                targets: [3]
            }
        ]
    });
});
