dt-example.js:
$('#example').DataTable({
    columnDefs: [
        {
            searchPanes: {
                header: 'Length of Life'
            },
            targets: [3]
        }
    ],
    layout: {
        top1: 'searchPanes'
    }
});

dt-example.js-vanilla:
new DataTable('#example', {
    columnDefs: [
        {
            searchPanes: {
                header: 'Length of Life'
            },
            targets: [3]
        }
    ],
    layout: {
        top1: 'searchPanes'
    }
});
