dt-example.css:
div.dt-searchPane div.dataTables_length{
    float: none;
    text-align: center;
}

dt-example.js:
$(document).ready(function() {
    $('#example').DataTable( {
        searchPanes:{
            dtOpts:{
                dom:"tp",
                paging: true,
                pagingType: 'numbers',
                searching: false,
            }
        },
        dom: 'Plfrtip',
        columnDefs: [
            {
                searchPanes:{
                    dtOpts:{
                        dom:"ti",
                        info: true,
                    }
                },
                targets: [2]
            }
        ]
    });
});
