dt-example.css:
    div.dtsp-searchPanes div.dt-container div.dt-paging,
	div.dtsp-searchPanes div.dt-container div.dt-info {
		width: fit-content;
		margin: 0 auto;
		min-height: 3em;
    }

dt-example.js:
$('#example').DataTable({
	layout: {
		top1: {
			searchPanes: {
				dtOpts: {
					layout: {
						bottom: {
							paging: {
								type: 'numbers'
							}
						}
					},
					paging: true,
					searching: false
				}
			}
		}
	},
	columnDefs: [
		{
			searchPanes: {
				dtOpts: {
					layout: {
						bottom: 'info'
					},
					info: true
				}
			},
			targets: [2]
		}
	]
});

dt-example.js-vanilla:
new DataTable('#example', {
	layout: {
		top1: {
			searchPanes: {
				dtOpts: {
					layout: {
						bottom: {
							paging: {
								type: 'numbers'
							}
						}
					},
					paging: true,
					searching: false
				}
			}
		}
	},
	columnDefs: [
		{
			searchPanes: {
				dtOpts: {
					layout: {
						bottom: 'info'
					},
					info: true
				}
			},
			targets: [2]
		}
	]
});
