dt-example.css:
div.dt-searchPane div.dataTables_length{
    float: none;
    text-align: center;
}

dt-example.js:
$(document).ready(function() {
    $('#example').DataTable( {
        searchPanes:{
            dtOpts:{
                paging: true,
                pagingType: 'numbers', 
            },
        },
        dom: 'Sfrtip',
        columnDefs: [

            {
                searchPanes:{
                    dtOpts:{
                        info: true,
                    }
                },
                targets: [2]
            },
        ]
    });
});
