dt-example.js:
$('#example').DataTable({
	layout: {
		top1: 'searchPanes'
	},
	language: {
		searchPanes: {
			title: {
				_: 'Filters Selected - %d',
				0: 'No Filters Selected',
				1: 'One Filter Selected'
			}
		}
	}
});

dt-example.js-vanilla:
new DataTable('#example', {
	layout: {
		top1: 'searchPanes'
	},
	language: {
		searchPanes: {
			title: {
				_: 'Filters Selected - %d',
				0: 'No Filters Selected',
				1: 'One Filter Selected'
			}
		}
	}
});
