dt-example.js:
$(document).ready(function() {
    $('#example').DataTable( {
        searchPanes:{
            layout: 'columns-1',
            dataLength: 20
        },
        responsive: true,
        dom: '<"dtsp-verticalContainer"<"dtsp-verticalPanes"P><"dtsp-dataTable"frtip>>',
        pageLength: 20
    });
});
