dt-example.js:
$(document).ready(function() {
    var dt = $('#example').DataTable( {
        searchPanes: {
            minRows:10,
            displayColumns:2,
        },
        dom: 'Sfrtip',
    });
    $('#Reduce').click(function(){
        dt.rows(":gt(7)").remove().draw().searchPanes.rebuild();
    });
});
