    $('#example').DataTable( {
        searchPanes:{
            threshold: 0.4,
            layout: 'columns-2',
        },
        dom: 'Sfrtip',
        columnDefs:[
            {
                searchPanes:{
                    threshold: 1,
                },
                targets:[0]
            },
        ],
    });
});
