dt-example.js:
$(document).ready(function() {
    $('#example').DataTable({
        searchPanes: {
            layout: 'columns-6'
        },
        dom: 'Plfrtip',
        columnDefs: [
            {
                searchPanes: {
                    show: true
                },
                targets: [0, 1, 2, 3, 4, 5]
            }
        ]
    });
});
