dt-example.js:
$(document).ready( function () {
  var table = $('#example').DataTable({
      columnDefs:[
      {
          searchPanes:{
              show: false,
          },
          targets: [1, 2],
      }
    ],
    buttons: {
      dom: {
        container: {
          className: 'dt-buttons btn-group floatRight'
        }                        
      },
      buttons: [{
                text: 'My button',
                action: function ( e, dt, node, config ) {
                  console.log(e);
                    e.stopPropogation();
                    this.popover('<div><button>Im a button</button></div>', {
                      align: 'dt-container'
                    });
                }
            }, 'searchPanes']
    },
    dom: 'Brtip',
  });
});
