dt-example.js:
$(document).ready(function() {
    $('#example').DataTable( {
        ajax: '../objects.json',
        searchPane:{
            threshold:0
        },
        columns: [
			{ data: null, render: function ( data, type, row ) {
				// Combine the first and last names into a single table field
				return data.first_name+' '+data.last_name;
			} },
			{ data: "position" },
			{ data: "office" },
			{ data: "extn" },
			{ data: "start_date" },
			{ 
				data: "salary", 
				render: $.fn.dataTable.render.number( ',', '.', 0, '$' ) 
			}
		]
    });
});
