dt-example.js:
$(document).ready(function() {
    $('#example').DataTable( {
        ajax: '../arrayData.json',
        searchPane:{
            threshold:0
        },
        columns: [
			{ 
				data: "users.first_name"
			},
			{ 
				data: "users.last_name"
			},
			{ 
				data: "sites.name"
			},
			{ 
				data: "permission",
				render: {
					_: '[, ].name',
					sp:'[].name'
				},
				searchPane: {
					orthogonal:'sp',
					match:'any'
				}
			},
		]
    });
});
