dt-example.js:
$(document).ready(function() {
    $('#example').DataTable( {
        searchPane: {
            cascadePanes: true,
            viewTotal: true,
        },
        columnDefs:[ 
            {
                orderable: false,
                className: 'select-checkbox',
                targets:   0
            },
            {
                searchPane: {
                    options:[
                        {
                            label: 'Over 60',
                            value: 60,
                            condition:'>',
                        },
                        {
                            label: '20 to 30',
                            value: function(rowData){
                                return rowData.filter <= 30 && rowData.filter >=20;
                            }
                        }
                    ]
                },
                targets:[4],
            },
            {
                searchPane: {
                    options:[
                        {
                            label: 'Not Edinburgh',
                            value: 'Edinburgh',
                            condition: '!=',
                        },
                        {
                            label: 'Not London',
                            value: function(rowData){
                                return rowData.filter !== 'London';
                            }
                        }
                    ],
                },
                targets: [3]
            }
        ],
        select: {
			style:    'os',
			selector: 'td:first-child'
		},
		order: [[ 1, 'asc' ]]
    });
});
