dt-example.js:
$(document).ready(function() {
    var dt = $('#example').DataTable( {
        searchPane:{
            emptyMessage:"<i><b>EMPTY</b></i>",
        },
        columnDefs:[
            {
                searchPane: {
                    match:"any"
                },
                targets: ["_all"]
            }
        ]
    });

    dt.cell(2,3).data('').draw();
    dt.cell(4,3).data('Edi').draw();
    dt.cell(6,3).data('').draw();
    dt.cell(2,4).data('').draw();
    dt.searchPanes.rebuild();
});
