dt-example.js:
$(document).ready(function() {
    var dt = $('#example').DataTable( {
        searchPane: {
            viewTotal: true,
        },
        columnDefs:[ 
            {
                orderable: false,
                className: 'select-checkbox',
                searchPane: {
                    show: true,
                    options:[
                        {
                            label: 'Checked',
                            value: function(rowData,rowIdx){
                                console.log(this.row(rowIdx, {selected: true}).any())
                                return this.row(rowIdx, {selected: true}).any();
                            },
                            successReplace: 'true'
                        },
                        {
                            label: 'Un-Checked',
                            value: function(rowData, rowIdx){
                                console.log(this.row(rowIdx, {selected: true}).any() === false)
                                return this.row(rowIdx, {selected: true}).any() === false;
                            },
                            successReplace: 'false'
                        },
                    ],
                },
                targets: [0]
            },
        ],
        select: {
			style:    'os',
			selector: 'td:first-child'
		},
		order: [[ 1, 'asc' ]]
    });

    dt.on('select.dt', () => {			
        dt.searchPane.rebuildPane(0);
    });

    dt.on('deselect.dt', () => {
        dt.searchPane.rebuildPane(0);
    });
});
