dt-example.js:
$(document).ready(function() {
    $('#example').DataTable( {
        searchPane:{
            viewTotal: true,
        },
        columnDefs:[
            {
                searchPane: {
                    comparison:[
                        {
                            label: 'Over 60',
                            value: 60,
                            condition:'>',
                        },
                    ]
                },
                targets:[4],
            },
            {
                searchPane: {
                    comparison:[
                        {
                            label: 'Not Edinburgh',
                            value: 'Edinburgh',
                            condition: '!=',
                        },
                        {
                            label: 'UK',
                            value:['Edinburgh', 'London'],
                            condition:'||',
                        },
                    ],
                },
                targets: [3]
            },
            {
                searchPane: {
                    comparison:[
                        {
                            label:'Chief',
                            value: 'Chief',
                            condition: 'includes',
                        },
                    ],
                },
                targets: [2],
            }
        ]
    });
});
