dt-example.js:
$(document).ready(function() {
    $('#example').DataTable( {
        searchPane:true,
        columnDefs:[
            {
                data: 'location',
                render: function (data, type) {
                    if (type === 'sort' ) {
                        return data;
                    }
                    else if (type === 'filter') {
                        return  (data === 'Edinburgh' || data=== 'London' ? 'UK' : 'Rest of World');
                    }
                    return (data === 'Edinburgh' || data=== 'London' ? 'UK' : 'Rest of World')+' ('+data+')'
                },

                searchPane: {
                    orthogonal: 'display',

                    orthogonal: '[].display',

                    orthogonal: {
                        display: 'vdu',
                        filter: 'sear'
                    }
                },
                targets:[3]
            }
        ]
    });
});
