dt-example.js:
$(document).ready(function() {
	$('#example').DataTable({
		dom: 'Plfrtip',
		searchPanes: {
			cascadePanes: true
		},
		ajax: '../../../../examples/ajax/data/data_50k.txt',
		columns: [
			{ data: "name" },
			{ data: "position" },
			{ data: "office" },
			{ data: "extn" },
			{ data: "start_date" },
			{ data: "salary" }
		],
        deferRender: true
	});
});
