dt-example.js:
$(document).ready(function() {
    $('#example').DataTable( {
        dom: 'Pfrtip',
		searchPanes: {
			cascadePanes: true,
			viewTotal: true
		},
		ajax: '../../../../examples/ajax/data/data_10k.txt',
		columns: [
            { data: "name" },
            { data: "position" },
            { data: "office" },
            { data: "extn" },
            { data: "start_date" },
            { data: "salary" }
        ],
        deferRender: true
    });
});
