dt-example.js:
$(document).ready(function() {
    var dt = $('#example').DataTable( {
        searchPanes:{
            emptyMessage:"<i><b>EMPTY</b></i>",
        },
    });

    dt.cell(2,3).data('');
    dt.cell(4,3).data('Edi');
    dt.cell(6,3).data('');
    dt.cell(2,4).data('');
    console.log("rebuild")
    dt.searchPanes.rebuildPane(3);
    dt.searchPanes.rebuildPane(4);
});
