$(document).ready(function() {
    $('#example td').eq(2).html('&');
    $('#example td').eq(11).html('&');
    $('#example td').eq(21).html('<');
    $('#example td').eq(30).html('<');
    $('#example td').eq(40).html('>');
    $('#example td').eq(49).html('>');
    $('#example td').eq(57).html('€');
    $('#example td').eq(66).html('€');
