dt-example.js:
$(document).ready(function() {
    var dt = $('#example').DataTable( {
        searchPanes:{
            threshold: 1
        },
		ajax: "../arrayData.json",
		columns: [
			{ data: "users.first_name" },
            { data: "users.last_name" },
            { data: "sites.name" },
            { data: "permission", render: "[].name" }


            { data: "permission", render: "[, ].name", searchPanesData: '[].name' }

            {
                data: "permission",
                render: {
                    _: "[, ].name",
                    searchPanesDisplay: '[].name'
                },
                searchPanes: {
                    match: 'any'
                }
            }

            {
                data: "ip",
                render: '[, ].name',
                searchPanes: {
                    orthogonal: '[].name'
                }



                searchPanes: {
                    orthogonal: {
                        search: '[].name',
                        display: '[].name8'
                }
            }


            {
                data: 'location',
                render: function (data) {
                    return data === 'Edinburgh' || data=== 'London' ? 'UK' : 'Rest of World'
                }
            }
		]
	});
});
